msg.topic =
    "INSERT INTO `database`(`index_no`, `comp`, `name`, `email`, `ic`, `staff_id`, `license_d`, `license_b2`, `phone`, `uid`, `password`) " +
    "VALUES ("+index_no+",'"+comp+"','"+name+"','"+email+"','"+ic+"','"+staff_id+"',"+license_d+","+license_b2+",'"+phone+"','"+new_uid+"','"+password+"')";

