// Get registrationInProgress, if not in context flow, set as false
let inProgress = flow.get("registrationInProgress") || false;

// But if registrationInProgress is in flow context and its true,
// then block scanned UID
if (inProgress) {
    node.warn("Registration in progress. UID rejected.");
    return null;
}

// Else, registrationInProgress is false
// and scanned UID is stored in flow context
flow.set("new_uid", msg.payload);

// Set registrationInProgress as true in flow context
// to block other UID scan
flow.set("registrationInProgress", true);

return msg;

// Remove new_uid from flow context
flow.set("new_uid", null);

// Set registration as not in progress
flow.set("registrationInProgress", false);

return msg;

// Set form active
flow.set("form_enabled", true);

// Pass data if value isn't empty
if (msg.payload !== "") {

    flow.set("uid", msg.payload);

    const ts_in = Date.now();
    const dateObject = new Date(ts_in);

    const date_in = dateObject.toLocaleString('en-GB', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric'
    }); // Full date and time
    const time_in = dateObject.toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true // Use 24-hour format
    })

    // Save all below in flow context
    flow.set("ts_in", ts_in);
    flow.set("date_in", date_in);
    flow.set("time_in", time_in);

    msg.payload = {
        value: msg.payload
    };

    return msg;
}

var name = msg.payload.name;
var staff_id = msg.payload.staff_id;
var time_in = msg.payload.time_in;

return msg;

flow.set("station", msg.payload);

let enabled = flow.get("form_enabled") || false;
if (!enabled) {
    return null; // discard or notify user
}
return msg; // allow

flow.set("form_enabled", false);
flow.set("uid", null);
flow.set("ts_in", null);
flow.set("date_in", null);
flow.set("time_in", null);
