// optionally clear scanned staff data
return msg;

flow.set("staff_id", msg.payload.staff_id);
flow.set("password", msg.payload.password);

return msg;

// Get new_uid from flow context
let new_uid = flow.get("new_uid");

// Get registrationInProgress status
// If not found in flow context, set registration as not in progress
let inProgress = flow.get("registrationInProgress") || false;

// Block if no UID was scanned before form submission
if (!new_uid) {
    node.warn("UID not scanned. Registration blocked.");
    return null;
}

// Store scanned UID and mark registration as in progress
flow.set("registrationInProgress", true);

// Store form data
flow.set("comp", msg.payload.comp);
flow.set("name", msg.payload.name);
flow.set("email", msg.payload.email);
flow.set("ic", msg.payload.ic);
flow.set("staff_id", msg.payload.staff_id);
flow.set("license_d", msg.payload.license_d);
