flow.set("phone", msg.payload.phone);
flow.set("password", msg.payload.password);

return msg;

let data = msg.payload;  // array of user records from the database
let staff_id = flow.get("staff_id");  // from earlier function
let password = flow.get("password");  // from earlier function

flow.set("login_success", false);  // always reset before checking

for (let i = 0; i < data.length; i++) {
    if (data[i].staff_id &&
        data[i].staff_id.toString().trim() === staff_id.toString().trim() &&
        data[i].password === password) {

        flow.set("login_success", true);
        flow.set("user", data[i]);  // optional: store full user info
        return msg;
    }
}

return null;  // login failed

let scanIn = flow.get("scanIn");
let scanOut = msg.payload;
const selectedDate = flow.get("selectedDate");
const targetCompany = "SL Leader Enterprise";

// Ensure arrays
if (!Array.isArray(scanIn)) scanIn = [scanIn].filter(Boolean);
if (!Array.isArray(scanOut)) scanOut = [scanOut].filter(Boolean);

let results = [];

scanIn.forEach(inRow => {
    if (
        inRow["Scan Date In"] !== selectedDate ||
        inRow["Company"] !== targetCompany
    ) return;

    const match = scanOut.find(outRow =>
        outRow["QR ID"] === inRow["QR ID"] &&
        outRow["Scan Date Out"] === selectedDate
    );

    const inTime = parseInt(inRow["Scan Timestamp In"]);
    const outTime = match ? parseInt(match["Scan Timestamp Out"]) : null;

    const duration = outTime ? ((outTime - inTime) / (1000 * 60 * 60)).toFixed(2) : "0.00";

    results.push({
        Name: inRow["Full Name"],
        Company: inRow["Company"],
        Date: selectedDate,
        TimeIn: inRow["Scan Time In"],
        TimeOut: match ? match["Scan Time Out"] : "N/A",
        Hours: duration
    });
});

msg.payload = results;
return msg;

let user = flow.get("user");

if (!user || !user.role) {
    node.warn("User role not set");
    return null;
}

let target_tab = "";

if (user.role === "admin") {
    target_tab = "admin_tab"; // must match the tab name exactly
} else {
    target_tab = "user_tab";  // must match the tab name exactly
}

return {
    payload: {
        tab: target_tab
    }
};

let maxIndex = msg.payload[0].max_index || 0;
let index_no = maxIndex + 1;

flow.set("index_no", index_no);
msg.payload = index_no;

return msg;

// Set data from database as data
let data = msg.payload;

// Set status to find as false first
let found = false;

// Recall all related data from flow context
let uid = flow.get("uid");
let index_no = flow.get("scan_index_no");
let date_in = flow.get("date_in");
let time_in = flow.get("time_in");
let ts_in = flow.get("ts_in");

for (let i = 0; i < data.length; i++) {
    if (data[i].uid &&
        data[i].uid.toString().trim() === uid.toString().trim()) {
        msg.payload = {
            index_no: index_no,
            comp: data[i].comp,
            name: data[i].name,
            staff_id: data[i].staff_id,
            uid: uid,
            date_in: date_in,
            time_in: time_in,
            ts_in: ts_in,
            completeRecord: true
        };
        found = true;
        return [msg, null];
    }
}

if (!found) {
    // Clear variables
    flow.set("uid", null);
    flow.set("index_no", null);
    flow.set("date_in", null);
    flow.set("time_in", null);
    flow.set("ts_in", null);
    flow.set("date_out", null);
    flow.set("time_out", null);
    flow.set("ts_out", null);
    msg.topic = `INSERT INTO system_log (timestamp, action_type, uid, message) VALUES (NOW(), 'ScanIn', '${uid}', 'UID not found in staff database')`;
    return [null, msg];
}

// Assumes msg.payload is an array from the database
let data = msg.payload;

// Get last row
let last = data[data.length - 1];
let last_index = 0;

// Checks if last exists and has an index_no field.
// If it does, last_index is set to that value.
if (last && last.index_no !== undefined && last.index_no !== null) {
    last_index = last.index_no;
}

// Store next index_no in flow context
let next_index = last_index + 1;
flow.set("scan_index_no", next_index);

msg.payload = null;
return msg;

// Save uid in flow context
flow.set("uid", msg.payload);

// Get current time
const ts_out = Date.now();
const dateObj = new Date(ts_out);

// Save scan out details in flow context
flow.set("ts_out", ts_out);
flow.set("date_out", dateObj.toLocaleDateString('en-GB'));
flow.set("time_out", dateObj.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: true }));

return msg;

// Recall uid from flow context
const uid = flow.get("uid");

// Let database as data
const data = msg.payload;

// Find uid in database
if (data.find(row => row.uid === uid)) {
  // If found, search for ts_in
  return [msg, null];
} else {
  // If not found, log in system log
  msg.topic = `INSERT INTO system_log (timestamp, action_type, uid, message) VALUES (NOW(), 'ScanOut', '${uid}', 'UID not found in staff database')`;
  return [null, msg];
}

// Get UID from flow context
const uid = flow.get("uid");
const ts_out = flow.get("ts_out");

// Calculate timestamp for 24 hours ago
const ts_24h_ago = ts_out - (24 * 60 * 60 * 1000);

// Build SQL to find the latest scan_in within 24h
msg.topic = `
    SELECT *
    FROM attendance
    WHERE uid = '${uid}'
      AND ts_in >= ${ts_24h_ago}
      AND ts_in <= ${ts_out}
      AND (ts_out IS NULL OR ts_out = '')
    ORDER BY ts_in DESC
    LIMIT 1
`;

return msg;

const match = msg.payload[0];
if (!match) return null;
flow.set("latestMatch", match);
return msg;

const ts_out = flow.get("ts_out");
const time_out = flow.get("time_out");
const date_out = flow.get("date_out");
const match = flow.get("latestMatch");

const index_no = match.index_no;
flow.set("match.index_no", index_no);
const ts_in = Number(match.ts_in);

const totalMinutes = Math.floor((ts_out - ts_in) / 60000);
const hours = Math.floor(totalMinutes / 60);
const minutes = totalMinutes % 60;

const total_worktime = `${hours}h ${minutes}m`;

msg.topic = `UPDATE attendance 
             SET date_out='${date_out}', 
                 time_out='${time_out}', 
                 ts_out=${ts_out}, 
                 total_worktime='${total_worktime}' 
             WHERE index_no=${index_no}`;

return msg;

const uid = flow.get("uid");
const match = flow.get("latestMatch");

msg.topic = `INSERT INTO system_log 
             (timestamp, action_type, uid, message) 
             VALUES (NOW(), 'ScanOut', '${uid}', 'Updated scan-out for index_no ${match.index_no}')`;

return msg;

// Get the station and index number from flow context
let station = flow.get("station");
let index_no = flow.get("scan_index_no");

// --- Output 1: Send station to display ---
let displayMsg = {
    payload: station
};

// --- Output 2: Update database ---
let dbMsg = {
    topic: `UPDATE attendance 
            SET station = '${station}'
            WHERE index_no = ${index_no}`
};

// Return both outputs: 
// Output 1  Display station node
// Output 2  MySQL update node
return [displayMsg, dbMsg];

// Recall new_uid as msg.payload
msg.payload = flow.get("new_uid");

return msg;

var data = msg.payload[0]; // first row from SELECT
if (!data) return null; // no row found

let display_all = {
    name: data.name,
    staff_id: data.staff_id,
    time_in: data.time_in,
    time_out: data.time_out,
    total_worktime: data.total_worktime
};

let display_station = data.station;
flow.set("station_calc", data.station);

let msg1 = { payload: display_all };
let msg2 = { payload: display_station };

return [msg1, msg2];

// Get msg.payload data
var index_no = msg.payload.index_no;
var comp = msg.payload.comp;
var name = msg.payload.name;
var staff_id = msg.payload.staff_id;
var station = null;
var uid = msg.payload.uid;
var date_in = msg.payload.date_in;
var time_in = msg.payload.time_in;
var ts_in = msg.payload.ts_in;
var date_out = null;
var time_out = null;
var ts_out = null;
var total_worktime = null;

msg.topic =
    "INSERT INTO `attendance`(`index_no`, `comp`, `name`, `staff_id`, `station`, `uid`, `date_in`, `time_in`, `ts_in`, `date_out`, `time_out`, `ts_out`,`total_worktime`) " +
    "VALUES ("+index_no+",'"+comp+"','"+name+"','"+staff_id+"','"+station+"','"+uid+"','"+date_in+"','"+time_in+"',"+ts_in+",'"+date_out+"','"+time_out+"',"+ts_out+",'"+total_worktime+"')";

return msg;

let match = flow.get("match");
let index_no = match.index_no;

msg.topic = `SELECT * FROM attendance WHERE index_no = ${index_no}`;

return msg;

const uid = flow.get("uid");
const index_no = flow.get("scan_index_no");

msg.topic = `INSERT INTO system_log 
             (timestamp, action_type, uid, message) 
             VALUES (NOW(), 'ScanIn', '${uid}', 'Updated scan-in for index_no ${index_no}')`;

return msg;

if (!msg.payload || msg.payload.length === 0) return null;

const row = msg.payload[0];
const staff_id = row.staff_id;
const station = row.station;

// minutes  hours (2 decimals). toFixed returns string; wrap with Number(...)
const minutes = Number(row.work_minutes);
if (!Number.isFinite(minutes) || minutes <= 0) {
    node.warn("Invalid work_hours (minutes was null/NaN/<=0). Skipping update.");
    return null;
}
const work_hours = Number((minutes / 60).toFixed(2));

// Build UPSERT with level computed from cumulative hours
msg.topic = `
INSERT INTO skills (staff_id, station, level, work_hours, last_updated)
VALUES ('${staff_id}', '${station}', 1, ${work_hours}, NOW())
ON DUPLICATE KEY UPDATE
  work_hours   = work_hours + ${work_hours},
  last_updated = NOW(),
  level = CASE
             WHEN work_hours + ${work_hours} >= 32 THEN 4
             WHEN work_hours + ${work_hours} >= 24 THEN 3
             WHEN work_hours + ${work_hours} >= 16 THEN 2
             ELSE 1
          END;
`;
return msg;

// Recall UID and Station from flow
const uid = flow.get("uid") || msg.payload;
const station = flow.get("station") || 'Trim';

msg.topic = `SELECT staff_id, station, ts_in, ts_out,
  TIMESTAMPDIFF(
    MINUTE,
    FROM_UNIXTIME(CASE WHEN ts_in > 9999999999 THEN ts_in / 1000 ELSE ts_in END),
    FROM_UNIXTIME(CASE WHEN ts_out > 9999999999 THEN ts_out / 1000 ELSE ts_out END)
  ) AS work_minutes
FROM attendance
WHERE uid='${uid}'
AND station='${station}'
AND ts_in IS NOT NULL
AND ts_out IS NOT NULL
AND ts_out > ts_in
ORDER BY ts_out DESC
LIMIT 1;`;
return msg;

// Extract latest attendance row
if (!msg.payload || msg.payload.length === 0) return null;
const row = msg.payload[0];
const minutes = Number(row.work_minutes);
if (!Number.isFinite(minutes) || minutes <= 0) {
    node.warn(`Invalid work_hours for UID ${row.staff_id}. Skipping update.`);
    return null;
}

// Read current skill record from flow or SQL (assume flow)
const currentSkill = flow.get(`skill_${row.staff_id}_${row.station}`) || {level:1, work_hours:0};

let newWorkHours = currentSkill.work_hours + minutes / 60;
let newLevel = currentSkill.level;
if (newWorkHours >= 8 && currentSkill.level < 4) {
    newLevel += 1;
    newWorkHours = 0; // reset hours for next level
}

// Update flow context
flow.set(`skill_${row.staff_id}_${row.station}`, {level:newLevel, work_hours:newWorkHours});

msg.topic = `INSERT INTO skills (staff_id, station, level, work_hours, last_updated)
  VALUES ('${row.staff_id}','${row.station}',${newLevel},${newWorkHours},NOW())
  ON DUPLICATE KEY UPDATE level=${newLevel}, work_hours=${newWorkHours}, last_updated=NOW();`;
return msg;

const row = flow.get(`skill_${flow.get('uid') || 'AS1234'}_Trim`);
msg.topic = `INSERT INTO system_log (timestamp, action_type, uid, message) VALUES (NOW(),'SkillUpdate','${flow.get('uid') || 'AS1234'}','Updated skill for station Trim to level ${row.level}, work_hours ${row.work_hours}')`;
return msg;
